import React, { useState } from 'react';
import { products } from '../data/products';

const AdminDashboard = () => {
    const [allProducts, setAllProducts] = useState(products);
    const [newProduct, setNewProduct] = useState({ title: "", price: "", image: "", category: "", gender: "", size: "", description: "" });

    const handleAddProduct = () => {
        setAllProducts(prev => [...prev, { ...newProduct, id: prev.length + 1 }]);
        setNewProduct({ title: "", price: "", image: "", category: "", gender: "", size: "", description: "" });
    };

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>
            <div className="admin-section">
                <h2>Manage Products</h2>
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Price</th>
                            <th>Category</th>
                        </tr>
                    </thead>
                    <tbody>
                        {allProducts.map(p => (
                            <tr key={p.id}>
                                <td>{p.id}</td>
                                <td>{p.title}</td>
                                <td>{p.price}</td>
                                <td>{p.category}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div className="admin-form">
                    <div className="admin-form-group">
                        <label>Title</label>
                        <input type="text" value={newProduct.title} onChange={e => setNewProduct({...newProduct, title: e.target.value})} />
                    </div>
                     <div className="admin-form-group">
                        <label>Price</label>
                        <input type="text" value={newProduct.price} onChange={e => setNewProduct({...newProduct, price: e.target.value})} />
                    </div>
                     <div className="admin-form-group">
                        <label>Image URL</label>
                        <input type="text" value={newProduct.image} onChange={e => setNewProduct({...newProduct, image: e.target.value})} />
                    </div>
                     <div className="admin-form-group">
                        <label>Category</label>
                        <input type="text" value={newProduct.category} onChange={e => setNewProduct({...newProduct, category: e.target.value})} />
                    </div>
                </div>
                 <button className="add-to-cart" onClick={handleAddProduct}>Add Product</button>
            </div>
        </div>
    );
};

export default AdminDashboard;
