import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const PaymentOptions = () => {
  const [card, setCard] = useState({ number: '', expiry: '', cvv: '' });

  const handleCardChange = (e) => {
    setCard({ ...card, [e.target.name]: e.target.value });
  };

  const isCardValid = () => {
    return card.number.length === 16 && card.expiry.match(/^(0[1-9]|1[0-2])\/\d{2}$/) && card.cvv.length === 3;
  };

  return (
    <div className="generic-page">
      <h1>Payment Options</h1>
      <div className="feature-grid">
        <Link to="/account/payment/upi" className="feature-card">
          <h3>UPI</h3>
        </Link>
        <div className="feature-card">
          <h3>Stripe</h3>
          <p>Stripe integration coming soon.</p>
        </div>
        <div className="feature-card">
          <h3>Debit/Credit Card</h3>
          <div className="admin-form">
            <div className="admin-form-group">
              <label>Card Number</label>
              <input type="text" name="number" onChange={handleCardChange} />
            </div>
            <div className="admin-form-group">
              <label>Expiry Date (MM/YY)</label>
              <input type="text" name="expiry" onChange={handleCardChange} />
            </div>
            <div className="admin-form-group">
              <label>CVV</label>
              <input type="text" name="cvv" onChange={handleCardChange} />
            </div>
            {isCardValid() && <button className="add-to-cart">Complete Payment</button>}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentOptions;
