import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { products } from '../data/products';

function Home({ cart, setCart }) { 
  const [activeCategory, setActiveCategory] = useState("all");
  const [filteredProducts, setFilteredProducts] = useState(products);
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    gender: 'all',
    size: 'all',
    sort: 'default'
  });

  useEffect(() => {
    let tempProducts = [...products];

    // 1. Filter by sidebar category
    if (activeCategory !== 'all') {
      tempProducts = tempProducts.filter(p => p.category === activeCategory);
    }

    // 2. Filter by gender from filter panel
    if (filters.gender !== 'all') {
      tempProducts = tempProducts.filter(p => p.gender === filters.gender);
    }

    // 3. Filter by size from filter panel
    if (filters.size !== 'all') {
      tempProducts = tempProducts.filter(p => p.size === filters.size);
    }

    // 4. Sort by price
    const parsePrice = (priceStr) => parseFloat(priceStr.replace('₹', '').replace(',', ''));

    if (filters.sort === 'low-to-high') {
      tempProducts.sort((a, b) => parsePrice(a.price) - parsePrice(b.price));
    } else if (filters.sort === 'high-to-low') {
      tempProducts.sort((a, b) => parsePrice(b.price) - parsePrice(a.price));
    }

    setFilteredProducts(tempProducts);

  }, [activeCategory, filters]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const clearFilters = () => {
    setFilters({
      gender: 'all',
      size: 'all',
      sort: 'default'
    });
    setActiveCategory('all');
  };

  const handleAddToCart = (id) => setCart((prev) => ({ ...prev, [id]: (prev[id] || 0) + 1 }));
  const handleIncrease = (id) => setCart((prev) => ({ ...prev, [id]: prev[id] + 1 }));
  const handleDecrease = (id) => {
    setCart((prev) => {
      if (prev[id] <= 1) {
        const newCart = { ...prev };
        delete newCart[id];
        return newCart;
      }
      return { ...prev, [id]: prev[id] - 1 };
    });
  };

  const departments = [...new Set(products.map(p => p.category).filter(Boolean))];

  return (
    <div className="home">
      <section className="main-content">
        <aside className="sidebar">
          <h3>Department</h3>
          <ul>
             <li className={activeCategory === 'all' ? "active-link" : ""}>
                <button onClick={() => setActiveCategory('all')}>ALL</button>
             </li>
            {departments.map((cat) => (
              <li key={cat} className={activeCategory === cat ? "active-link" : ""}>
                <button onClick={() => setActiveCategory(cat)}>{cat.replace("-", " ").toUpperCase()}</button>
              </li>
            ))}
          </ul>
        </aside>
        <div className="products-section">
          <div className="top-controls">
            <nav className="top-categories">
              <ul>
                <li><Link to="/deals"><button>DEALS</button></Link></li>
                <li><Link to="/customer-service"><button>CUSTOMER SERVICE</button></Link></li>
                <li><Link to="/registry"><button>REGISTRY</button></Link></li>
                <li><Link to="/gift-cards"><button>GIFT CARDS</button></Link></li>
                <li><Link to="/sell"><button>SELL</button></Link></li>
              </ul>
            </nav>
            <button className="filter-button" onClick={() => setShowFilters(!showFilters)}>
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><polygon points="22 3 2 3 10 12.46 10 19 14 21 14 12.46 22 3"></polygon></svg>
              Filters
            </button>
          </div>

          {showFilters && (
            <div className="filter-panel">
              <div className="filter-grid">
                <div className="filter-group">
                  <label htmlFor="gender-filter">Gender</label>
                  <select id="gender-filter" name="gender" value={filters.gender} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                    <option value="unisex">Unisex</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label htmlFor="department-filter">Department</label>
                  <select id="department-filter" name="category" value={activeCategory} onChange={(e) => setActiveCategory(e.target.value)}>
                    <option value="all">All</option>
                    {departments.map(cat => <option key={cat} value={cat}>{cat.replace("-", " ").toUpperCase()}</option>)}                  </select>
                </div>
                <div className="filter-group">
                  <label htmlFor="size-filter">Size</label>
                  <select id="size-filter" name="size" value={filters.size} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="S">Small</option>
                    <option value="M">Medium</option>
                    <option value="L">Large</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label htmlFor="sort-filter">Price</label>
                  <select id="sort-filter" name="sort" value={filters.sort} onChange={handleFilterChange}>
                    <option value="default">Default</option>
                    <option value="low-to-high">Low to High</option>
                    <option value="high-to-low">High to Low</option>
                  </select>
                </div>
              </div>
              <div className="filter-actions">
                <button className="clear-filters-btn" onClick={clearFilters}>Clear Filters</button>
              </div>
            </div>
          )}

          <div className="products-grid">
            {filteredProducts.map((product) => (
              <Link to={`/product/${product.id}`} key={product.id} className="product-card" style={{textDecoration: 'none', color: 'inherit'}}>
                <img src={product.image} alt={product.title} onError={(e) => { e.target.onerror = null; e.target.src='https://placehold.co/200x200/f0f0f0/333?text=Image+Not+Found'; }}/>
                <h3>{product.title}</h3>
                <div className="price">{product.price}</div>
                {!cart[product.id] ? (
                  <button className="add-to-cart" onClick={(e) => {e.preventDefault(); handleAddToCart(product.id)}}>Add to Cart</button>
                ) : (
                  <div className="quantity-selector" onClick={(e) => e.preventDefault()}>
                    <button onClick={() => handleDecrease(product.id)}>-</button>
                    <span>{cart[product.id]}</span>
                    <button onClick={() => handleIncrease(product.id)}>+</button>
                  </div>
                )}
              </Link>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
}

export default Home;
