const ProductDetailPage = ({ cart, setCart }) => {
    const { id } = useParams();
    const product = products.find(p => p.id === parseInt(id));

    if (!product) {
        return <h2>Product not found</h2>;
    }

    const handleAddToCart = (id) => setCart((prev) => ({ ...prev, [id]: (prev[id] || 0) + 1 }));

    return (
        <div className="product-detail-page">
            <div className="product-detail-image">
                <img src={product.image} alt={product.title} />
            </div>
            <div className="product-detail-info">
                <h1>{product.title}</h1>
                <div className="price">{product.price}</div>
                <p className="description">{product.description}</p>
                <div className="product-detail-actions">
                    <button className="add-to-cart" onClick={() => handleAddToCart(product.id)}>Add to Cart</button>
                    <button className="add-to-cart buy-now">Buy Now</button>
                </div>
                <div className="reviews-section">
                    <h2>Customer Reviews</h2>
                    <div className="review">
                        <div className="review-author">John Doe</div>
                        <div className="review-rating">★★★★☆</div>
                        <p>Great product! Highly recommended.</p>
                    </div>
                    <div className="review">
                        <div className="review-author">Jane Smith</div>
                        <div className="review-rating">★★★★★</div>
                        <p>Excellent quality and fast shipping.</p>
                    </div>
                </div>
            </div>
        </div>
    );
};


const CartPage = ({ cart, setCart }) => {
    const [couponInput, setCouponInput] = useState("");
    const [appliedCoupon, setAppliedCoupon] = useState(null);
    const [couponError, setCouponError] = useState("");

    const cartItems = Object.keys(cart).map(id => {
        const product = products.find(p => p.id === parseInt(id));
        return { ...product, quantity: cart[id] };
    });

    const parsePrice = (priceStr) => parseFloat(priceStr.replace('₹', '').replace(',', ''));

    const subtotal = cartItems.reduce((total, item) => {
        return total + (parsePrice(item.price) * item.quantity);
    }, 0);

    const discount = appliedCoupon ? (subtotal * (couponCodes[appliedCoupon] / 100)) : 0;
    const totalCost = subtotal - discount;

    const handleApplyCoupon = () => {
        if (couponCodes[couponInput]) {
            setAppliedCoupon(couponInput);
            setCouponError("");
        } else {
            setCouponError("Invalid coupon code.");
            setAppliedCoupon(null);
        }
    };

    const handleIncrease = (id) => {
        setCart((prev) => ({ ...prev, [id]: prev[id] + 1 }));
    };

    const handleDecrease = (id) => {
        setCart((prev) => {
            if (prev[id] <= 1) {
                const newCart = { ...prev };
                delete newCart[id];
                return newCart;
            }
            return { ...prev, [id]: prev[id] - 1 };
        });
    };

    const handleRemove = (id) => {
        setCart(prev => {
            const newCart = {...prev};
            delete newCart[id];
            return newCart;
        })
    }

    if (cartItems.length === 0) {
        return (
            <div className="cart-page">
                <div className="empty-cart">
                    <h2>Your cart is empty</h2>
                    <p>Looks like you haven't added anything to your cart yet.</p>
                    <Link to="/">Continue Shopping</Link>
                </div>
            </div>
        )
    }

    return (
      <div className="cart-page">
        <div className="cart-items-container">
            <h2>Shopping Cart</h2>
            {cartItems.map(item => (
                <div key={item.id} className="cart-item">
                    <img src={item.image} alt={item.title} className="cart-item-img"/>
                    <div className="cart-item-details">
                        <h3 className="cart-item-title">{item.title}</h3>
                        <p className="cart-item-price">{item.price}</p>
                        <div className="cart-item-actions">
                            <div className="quantity-selector">
                                <button onClick={() => handleDecrease(item.id)}>-</button>
                                <span>{item.quantity}</span>
                                <button onClick={() => handleIncrease(item.id)}>+</button>
                            </div>
                            <button onClick={() => handleRemove(item.id)} className="clear-filters-btn">Remove</button>
                        </div>
                    </div>
                    <p className="cart-item-subtotal">₹{(parsePrice(item.price) * item.quantity).toFixed(2)}</p>
                </div>
            ))}
        </div>
        <div className="cart-summary">
            <h2>Order Summary</h2>
            <div className="summary-row">
                <span>Subtotal ({cartItems.reduce((acc, item) => acc + item.quantity, 0)} items)</span>
                <span>₹{subtotal.toFixed(2)}</span>
            </div>
             <div className="summary-row">
                <span>Shipping</span>
                <span>FREE</span>
            </div>
            {appliedCoupon && (
                <div className="summary-row discount-row">
                    <span>Discount ({couponCodes[appliedCoupon]}%)</span>
                    <span>- ₹{discount.toFixed(2)}</span>
                </div>
            )}
            <div className="summary-row summary-total">
                <span>Total</span>
                <span>₹{totalCost.toFixed(2)}</span>
            </div>
            <div className="coupon-container">
                <div className="coupon-input-group">
                    <input 
                        type="text" 
                        className="coupon-input" 
                        placeholder="Enter coupon code"
                        value={couponInput}
                        onChange={(e) => setCouponInput(e.target.value.toUpperCase())}
                    />
                    <button className="apply-coupon-btn" onClick={handleApplyCoupon}>Apply</button>
                </div>
                {couponError && <p className="coupon-message coupon-error">{couponError}</p>}
                {appliedCoupon && <p className="coupon-message coupon-success">Coupon "{appliedCoupon}" applied!</p>}
            </div>
            <button className="checkout-button">Proceed to Checkout</button>
        </div>
      </div>
    );
};


