const AdminDashboard = () => {
    const [allProducts, setAllProducts] = useState(products);
    const [newProduct, setNewProduct] = useState({ title: "", price: "", image: "", category: "", gender: "", size: "", description: "" });

    const handleAddProduct = () => {
        setAllProducts(prev => [...prev, { ...newProduct, id: prev.length + 1 }]);
        setNewProduct({ title: "", price: "", image: "", category: "", gender: "", size: "", description: "" });
    };

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>
            <div className="admin-section">
                <h2>Manage Products</h2>
                <table className="admin-table">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Price</th>
                            <th>Category</th>
                        </tr>
                    </thead>
                    <tbody>
                        {allProducts.map(p => (
                            <tr key={p.id}>
                                <td>{p.id}</td>
                                <td>{p.title}</td>
                                <td>{p.price}</td>
                                <td>{p.category}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <div className="admin-form">
                    <div className="admin-form-group">
                        <label>Title</label>
                        <input type="text" value={newProduct.title} onChange={e => setNewProduct({...newProduct, title: e.target.value})} />
                    </div>
                     <div className="admin-form-group">
                        <label>Price</label>
                        <input type="text" value={newProduct.price} onChange={e => setNewProduct({...newProduct, price: e.target.value})} />
                    </div>
                     <div className="admin-form-group">
                        <label>Image URL</label>
                        <input type="text" value={newProduct.image} onChange={e => setNewProduct({...newProduct, image: e.target.value})} />
                    </div>
                     <div className="admin-form-group">
                        <label>Category</label>
                        <input type="text" value={newProduct.category} onChange={e => setNewProduct({...newProduct, category: e.target.value})} />
                    </div>
                </div>
                 <button className="add-to-cart" onClick={handleAddProduct}>Add Product</button>
            </div>
        </div>
    );
};


// --- NAVBAR COMPONENT ---
const NavBar = ({ cart }) => {
  const [searchText, setSearchText] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const totalItems = Object.values(cart).reduce((sum, quantity) => sum + quantity, 0);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchText(value);
    if (!value.trim()) {
      setSuggestions([]);
      return;
    }
    const filtered = products
      .filter((p) => p.title.toLowerCase().includes(value.toLowerCase()))
      .slice(0, 5);
    setSuggestions(filtered);
  };

  const handleSuggestionClick = (title) => {
    setSearchText(title);
    setSuggestions([]);
  };

  return (
    <header className="navbar">
      <div className="navbar-content">
        <Link to="/" className="navbar-logo">Shopify</Link>
        <div className="navbar-search">
          <input type="text" placeholder="Search Shopify..." value={searchText} onChange={handleSearchChange} />
          <button className="search-button" aria-label="Search">
            <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="11" cy="11" r="8"></circle><line x1="21" y1="21" x2="16.65" y2="16.65"></line></svg>
          </button>
          {suggestions.length > 0 && (
            <ul className="search-suggestions">
              {suggestions.map((s) => (
                <li key={s.id} onClick={() => handleSuggestionClick(s.title)}>{s.title}</li>
              ))}
            </ul>
          )}
        </div>
        <nav className="navbar-nav">
          <Link to="/auth" className="nav-link">
            <span className="nav-line-one">Hello, Sign in</span>
            <span className="nav-line-two">Account & Lists</span>
          </Link>
          <Link to="/orders" className="nav-link">
            <span className="nav-line-one">Returns</span>
            <span className="nav-line-two">& Orders</span>
          </Link>
           <Link to="/admin" className="nav-link">
            <span className="nav-line-one">Admin</span>
            <span className="nav-line-two">Dashboard</span>
          </Link>
          <Link to="/cart" className="nav-link nav-cart">
             <svg xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="9" cy="21" r="1"></circle><circle cx="20" cy="21" r="1"></circle><path d="M1 1h4l2.68 13.39a2 2 0 0 0 2 1.61h9.72a2 2 0 0 0 2-1.61L23 6H6"></path></svg>
            <span className="cart-count">{totalItems}</span>
            <span className="nav-line-two cart-text">Cart</span>
          </Link>
        </nav>
      </div>
    </header>
  );
};

// --- HOME COMPONENT ---
function Home({ cart, setCart }) { 
  const [activeCategory, setActiveCategory] = useState("all");
  const [filteredProducts, setFilteredProducts] = useState(products);
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    gender: 'all',
    size: 'all',
    sort: 'default'
  });

  useEffect(() => {
    let tempProducts = [...products];

    // 1. Filter by sidebar category
    if (activeCategory !== 'all') {
      tempProducts = tempProducts.filter(p => p.category === activeCategory);
    }

    // 2. Filter by gender from filter panel
    if (filters.gender !== 'all') {
      tempProducts = tempProducts.filter(p => p.gender === filters.gender);
    }

    // 3. Filter by size from filter panel
    if (filters.size !== 'all') {
      tempProducts = tempProducts.filter(p => p.size === filters.size);
    }

    // 4. Sort by price
    const parsePrice = (priceStr) => parseFloat(priceStr.replace('₹', '').replace(',', ''));

    if (filters.sort === 'low-to-high') {
      tempProducts.sort((a, b) => parsePrice(a.price) - parsePrice(b.price));
    } else if (filters.sort === 'high-to-low') {
      tempProducts.sort((a, b) => parsePrice(b.price) - parsePrice(a.price));
    }

    setFilteredProducts(tempProducts);

  }, [activeCategory, filters]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  const clearFilters = () => {
    setFilters({
      gender: 'all',
      size: 'all',
      sort: 'default'
    });
    setActiveCategory('all');
  };

  const handleAddToCart = (id) => setCart((prev) => ({ ...prev, [id]: (prev[id] || 0) + 1 }));
  const handleIncrease = (id) => setCart((prev) => ({ ...prev, [id]: prev[id] + 1 }));
  const handleDecrease = (id) => {
    setCart((prev) => {
      if (prev[id] <= 1) {
        const newCart = { ...prev };
        delete newCart[id];
        return newCart;
      }
      return { ...prev, [id]: prev[id] - 1 };
    });
  };

  const departments = [...new Set(products.map(p => p.category).filter(Boolean))];

  return (
    <div className="home">
      <section className="main-content">
        <aside className="sidebar">
          <h3>Department</h3>
          <ul>
             <li className={activeCategory === 'all' ? "active-link" : ""}>
                <button onClick={() => setActiveCategory('all')}>ALL</button>
             </li>
            {departments.map((cat) => (
              <li key={cat} className={activeCategory === cat ? "active-link" : ""}>
                <button onClick={() => setActiveCategory(cat)}>{cat.replace("-", " ").toUpperCase()}</button>
              </li>
            ))}
          </ul>
        </aside>
        <div className="products-section">
          <div className="top-controls">
            <nav className="top-categories">
              <ul>
                <li><Link to="/deals"><button>DEALS</button></Link></li>
                <li><Link to="/customer-service"><button>CUSTOMER SERVICE</button></Link></li>
                <li><Link to="/registry"><button>REGISTRY</button></Link></li>
                <li><Link to="/gift-cards"><button>GIFT CARDS</button></Link></li>
                <li><Link to="/sell"><button>SELL</button></Link></li>
              </ul>
            </nav>
            <button className="filter-button" onClick={() => setShowFilters(!showFilters)}>
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><polygon points="22 3 2 3 10 12.46 10 19 14 21 14 12.46 22 3"></polygon></svg>
              Filters
            </button>
          </div>

          {showFilters && (
            <div className="filter-panel">
              <div className="filter-grid">
                <div className="filter-group">
                  <label htmlFor="gender-filter">Gender</label>
                  <select id="gender-filter" name="gender" value={filters.gender} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                    <option value="unisex">Unisex</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label htmlFor="department-filter">Department</label>
                  <select id="department-filter" name="category" value={activeCategory} onChange={(e) => setActiveCategory(e.target.value)}>
                    <option value="all">All</option>
                    {departments.map(cat => <option key={cat} value={cat}>{cat.replace("-", " ").toUpperCase()}</option>)}
                  </select>
                </div>
                <div className="filter-group">
                  <label htmlFor="size-filter">Size</label>
                  <select id="size-filter" name="size" value={filters.size} onChange={handleFilterChange}>
                    <option value="all">All</option>
                    <option value="S">Small</option>
                    <option value="M">Medium</option>
                    <option value="L">Large</option>
                  </select>
                </div>
                <div className="filter-group">
                  <label htmlFor="sort-filter">Price</label>
                  <select id="sort-filter" name="sort" value={filters.sort} onChange={handleFilterChange}>
                    <option value="default">Default</option>
                    <option value="low-to-high">Low to High</option>
                    <option value="high-to-low">High to Low</option>
                  </select>
                </div>
              </div>
              <div className="filter-actions">
                <button className="clear-filters-btn" onClick={clearFilters}>Clear Filters</button>
              </div>
            </div>
          )}

          <div className="products-grid">
            {filteredProducts.map((product) => (
              <Link to={`/product/${product.id}`} key={product.id} className="product-card" style={{textDecoration: 'none', color: 'inherit'}}>
                <img src={product.image} alt={product.title} onError={(e) => { e.target.onerror = null; e.target.src='https://placehold.co/200x200/f0f0f0/333?text=Image+Not+Found'; }}/>
                <h3>{product.title}</h3>
                <div className="price">{product.price}</div>
                {!cart[product.id] ? (
                  <button className="add-to-cart" onClick={(e) => {e.preventDefault(); handleAddToCart(product.id)}}>Add to Cart</button>
                ) : (
                  <div className="quantity-selector" onClick={(e) => e.preventDefault()}>
                    <button onClick={() => handleDecrease(product.id)}>-</button>
                    <span>{cart[product.id]}</span>
                    <button onClick={() => handleIncrease(product.id)}>+</button>
                  </div>
                )}
              </Link>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
}

// --- MAIN APP COMPONENT ---
function App() {
  const [cart, setCart] = useState({1: 2, 3: 1}); 

  return (
    <Router>
      <GlobalStyles />
      <NavBar cart={cart} />
      <main>
        <Routes>
          <Route path="/" element={<Home cart={cart} setCart={setCart} />} />
          <Route path="/product/:id" element={<ProductDetailPage cart={cart} setCart={setCart} />} />
          <Route path="/cart" element={<CartPage cart={cart} setCart={setCart} />} />
          <Route path="/auth" element={<AuthPage />} />
          <Route path="/orders" element={<OrdersPage />} />
          <Route path="/deals" element={<DealsPage />} />
          <Route path="/customer-service" element={<CustomerServicePage />} />
          <Route path="/registry" element={<RegistryPage />} />
          <Route path="/gift-cards" element={<GiftCardsPage />} />
          <Route path="/sell" element={<SellPage />} />
          <Route path="/admin" element={<AdminDashboard />} />
        </Routes>
      </main>
    </Router>
  );
}

export default App;
