import React from 'react';
import { useStateContext } from '../context/StateContext';

const LoginSecurity = () => {
  const { state, dispatch } = useStateContext();

  const handleLogout = () => {
    dispatch({ type: 'LOGOUT' });
  };

  const handleLogin = () => {
    dispatch({ type: 'LOGIN' });
  };

  const handleChange = (e) => {
    dispatch({ type: 'UPDATE_USER', payload: { [e.target.name]: e.target.value } });
  };

  return (
    <div className="generic-page">
      <h1>Login & Security</h1>
      {state.isAuthenticated ? (
        <button className="add-to-cart" onClick={handleLogout}>Logout</button>
      ) : (
        <button className="add-to-cart" onClick={handleLogin}>Login</button>
      )}

      <div className="admin-form" style={{ marginTop: '2rem' }}>
        <div className="admin-form-group">
          <label>Mobile Number</label>
          <input type="text" name="mobile" value={state.user.mobile} onChange={handleChange} />
        </div>
        <div className="admin-form-group">
          <label>Security</label>
          <input type="text" name="security" value={state.user.security} onChange={handleChange} />
        </div>
        <div className="admin-form-group">
          <label>Accessibility</label>
          <input type="text" name="accessibility" value={state.user.accessibility} onChange={handleChange} />
        </div>
      </div>
    </div>
  );
};

export default LoginSecurity;
