import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useStateContext } from '../context/StateContext';

const GiftCards = () => {
  const { dispatch } = useStateContext();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('buy');
  const [buyForm, setBuyForm] = useState({ amount: '', recipientName: '', recipientPhone: '', recipientEmail: '' });
  const [redeemCode, setRedeemCode] = useState('');

  const handleBuyChange = (e) => {
    setBuyForm({ ...buyForm, [e.target.name]: e.target.value });
  };

  const handleBuySubmit = (e) => {
    e.preventDefault();
    navigate('/account/payment');
  };

  const handleRedeem = () => {
    dispatch({ type: 'UPDATE_WALLET', payload: parseInt(redeemCode) });
    setRedeemCode('');
  };

  return (
    <div className="generic-page">
      <h1>Gift Cards</h1>
      <div style={{ display: "flex", gap: "1rem", marginBottom: "1.5rem" }}>
<button
  className={`small-btn ${activeTab === 'buy' ? 'active-tab' : ''}`}
  onClick={() => setActiveTab('buy')}
>
  Buy a Gift Card
</button>
  <button
    className={`small-btn ${activeTab === 'redeem' ? 'active-tab' : ''}`}
    onClick={() => setActiveTab('redeem')}
  >
    Redeem a Gift Card
  </button>
</div>

      {activeTab === 'buy' && (
        <form onSubmit={handleBuySubmit} className="admin-form" style={{ marginTop: '2rem' }}>
          <div className="admin-form-group">
            <label>Amount (â‚¹)</label>
            <input type="number" name="amount" onChange={handleBuyChange} />
          </div>
          <div className="admin-form-group">
            <label>Recipient Name (optional)</label>
            <input type="text" name="recipientName" onChange={handleBuyChange} />
          </div>
          <div className="admin-form-group">
            <label>Recipient Phone (optional)</label>
            <input type="text" name="recipientPhone" onChange={handleBuyChange} />
          </div>
          <div className="admin-form-group">
            <label>Recipient Email</label>
            <input type="email" name="recipientEmail" onChange={handleBuyChange} required />
          </div>
          <button type="submit" className="add-to-cart">Proceed to Payment</button>
        </form>
      )}

      {activeTab === 'redeem' && (
        <div className="admin-form" style={{ marginTop: '2rem' }}>
          <div className="admin-form-group">
            <label>Redeem Code</label>
            <input type="text" value={redeemCode} onChange={(e) => setRedeemCode(e.target.value)} />
          </div>
          <button className="add-to-cart" onClick={handleRedeem}>Redeem</button>
        </div>
      )}
    </div>
  );
};

export default GiftCards;
